const filename = msg.filename.toLowerCase();
const payload = msg.payload;

// Determine document type based on filename
let docType = 'UNKNOWN';
if (filename.includes('cor') || filename.includes('certificate')) {
    docType = 'COR';
} else if (filename.includes('invoice') || filename.includes('faktura')) {
    docType = 'INVOICE';
} else if (filename.includes('contract') || filename.includes('umowa')) {
    docType = 'CONTRACT';
}

msg.documentType = docType;
msg.originalFilename = msg.filename;
msg.fileSize = payload.length;
msg.processingStartTime = Date.now();

return msg;

const extractedText = msg.payload;
const docType = msg.documentType;

// Store extracted text
msg.extractedText = extractedText;

// Prepare analysis prompt based on document type
let prompt = '';
switch(docType) {
    case 'COR':
        prompt = `Analyze this Certificate of Conformity document. Extract key information:

${extractedText}

Return JSON:
{
  "certificate_number": "",
  "issuing_authority": "",
  "product_name": "",
  "compliance_status": "COMPLIANT|NON_COMPLIANT|UNCLEAR",
  "validity_start": "",
  "validity_end": "",
  "issues_found": [],
  "confidence_score": 0.95
}`;
        break;
    case 'INVOICE':
        prompt = `Extract invoice data:

${extractedText}

Return JSON:
{
  "invoice_number": "",
  "date": "",
  "supplier_name": "",
  "total_amount": 0.0,
  "currency": "",
  "due_date": "",
  "validation_status": "VALID|INVALID",
  "confidence_score": 0.95
}`;
        break;
    case 'CONTRACT':
        prompt = `Analyze contract for risks:

${extractedText}

Return JSON:
{
  "contract_type": "",
  "parties": [],
  "risk_level": "LOW|MEDIUM|HIGH",
  "key_terms": [],
  "risk_factors": [],
  "confidence_score": 0.95
}`;
        break;
    default:
        prompt = `Categorize this document:

${extractedText}

Return JSON:
{
  "category": "",
  "summary": "",
  "confidence_score": 0.95
}`;
}

msg.payload = {
    "model": "llama2:7b",
    "prompt": prompt,
    "stream": false
};

return msg;

try {
    const llmResponse = msg.payload;
    let analysis = {};

    // Try to parse JSON from LLM response
    if (llmResponse.response) {
        const jsonMatch = llmResponse.response.match(/\{[^}]+\}/);
        if (jsonMatch) {
            analysis = JSON.parse(jsonMatch[0]);
        } else {
            // Fallback
            analysis = {
                category: 'UNKNOWN',
                confidence_score: 0.5,
                summary: 'Could not parse LLM response'
            };
        }
    }

    msg.analysis = analysis;
    msg.processingTime = Date.now() - msg.processingStartTime;

    return msg;

} catch (error) {
    msg.error = error.message;
    msg.analysis = {
        category: 'ERROR',
        confidence_score: 0.0,
        summary: 'Analysis failed: ' + error.message
    };
    return msg;
}

msg.alert = {
    type: 'COMPLIANCE_ISSUE',
    severity: 'HIGH',
    document: msg.originalFilename,
    message: 'Compliance issues detected',
    timestamp: new Date().toISOString()
};

return msg;

msg.alert = {
    type: 'HIGH_VALUE_INVOICE',
    severity: 'MEDIUM',
    document: msg.originalFilename,
    message: 'High-value invoice requires approval',
    timestamp: new Date().toISOString()
};

return msg;

msg.alert = {
    type: 'CONTRACT_RISK',
    severity: 'HIGH',
    document: msg.originalFilename,
    message: 'High-risk contract detected',
    timestamp: new Date().toISOString()
};

return msg;

// Prepare document for Elasticsearch storage
const docForStorage = {
    filename: msg.originalFilename,
    document_type: msg.documentType,
    processing_timestamp: new Date().toISOString(),
    extracted_text: msg.extractedText,
    analysis_results: msg.analysis,
    file_size: msg.fileSize,
    processing_time_ms: msg.processingTime
};

msg.payload = docForStorage;
return msg;
